INSERT INTO `post` VALUES (68,19,1,'http://7xqx6h.com1.z0.glb.clouddn.com/7337989744285.jpg','','“西域之韵”手镯','“西域之韵”系列首饰的灵感源于宣礼塔的造型特征。宣礼塔，又称唤礼塔，是清真寺建筑的装饰艺术和标志之一。',NULL,'翔子，2013-11-04','',NULL,1);
